function envio(nombre,correo,titulo,mensaje){
const sgMail = require('@sendgrid/mail')
sgMail.setApiKey(process.env.SENDGRID_API_KEY)
var descripcion= mensaje + '\nEnviado por: ' + nombre + "\ncorreo: "+ correo
const msg = {
   to: 'raisita@hotmail.com', 
   from: 'hidalgo.raisa@gmail.com', 
   subject: titulo,
   text: descripcion,
   html: '<strong>Sent from rhidalgo.tk</strong>',
}
sgMail
   .send(msg)
   .then(() => {
   console.log('Email sent')
})
.catch((error) => {
console.error(error)
})
}